package com.syntax.ReplitsZone1;

public class ArithmeticOps1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Declare 2 numbers and assign values 200 and 100 respectively. First, add
		 * numbers, then subtract, third multiply, forth divide. Please use variables to
		 * print the values of each operation result on a separate line.
		 * 
		 */

		int num1 = 200;

		int num2 = 100;

		int add_Numbers = num1 + num2;

		int sub_Numbers = num1 - num2;

		int mult_Numbers = num1 * num2;

		int div_Numbers = num1 / num2;

		System.out.println(add_Numbers);

		System.out.println(sub_Numbers);

		System.out.println(mult_Numbers);

		System.out.println(div_Numbers);

	}

}
