package com.syntax.ReplitsZone1;

public class ArithmeticOps3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Add num1 and num2 Divide the result by num3 Subtract num4 from that result
		 * Instructions: Please use variables to assign the result of arithmetic
		 * operations
		 * 
		 * Output:
		 * 
		 * The result of arithmetic operations is equal to ___
		 * 
		 */

		int num1 = 3;
		int num2 = 7;
		int num3 = 5;
		int num4 = 1;

		// start coding here
		int result1 = (num1 + num2) / num3;

		int result2 = result1 - num4;

		System.out.println("The result of arithmetic operations is equal to " + result2);

	}

}
