package com.syntax.ReplitsZone2;

public class Forloop6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Write a program that prints the string "Hey, it is year: " and the years
		 * between 2011 and 2019. Must include year 2019 in the output
		 */

		int yearX = 2011;

		for (yearX = 2011; yearX <= 2019; yearX++) {
			System.out.println("Hey, it is year:");
			System.out.println(yearX);
		}
	}
}
