package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		/* Auto-generated method stub 
		*1. Create a variable or box to store a specific data type.
		*2. To create a variable we need:
		*		1. Specify dataType.
		*		2. Give it a name.
		*		
		*/
		
		//Below are Data types to represent numeric values
		byte box1 = 127; //Numerical value
		
		short box2 = 32765;
		
		int box3 = 324567846; //Most used 
		
		long box4 = 40548789383l; //Will be using
		
		//Data types to represent decimals 
		
		float price1 = 12.99F;
		
		double price2 = 13.99; //Most widely used and a larger data type
		
		char gender = 'F'; //Used to represent a single character
		
		char sign = '$';	//Used to represent a single character
		
		boolean snow = false; // Used to represent yes or no
		
		boolean rain = true;  // Used to represent yes or no
		
		System.out.println(box1);
		
		System.out.println(box2);
		
		System.out.println(box3);
		
		System.out.println(box4);
		
		System.out.println(price1);
		
		System.out.println(price2);
		
		System.out.println("price1");// The double quotes prints out a string value
		
		System.out.println(gender);
		
		System.out.println(sign);
		
		System.out.println(snow);
		
		System.out.println(rain);
		
		}

}
