package com.syntax.class02;

public class Task3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double Q1 = 100.25;                                                                                                     
		double Q2 = 20.20;                                                                                                      
		double Q3 = 3.9;                                                                                                        
		double sum = Q1 + Q2;                                                                                                   
		double sub = Q1 - Q2;                                                                                                   
		double multiply = Q1 * Q2;                                                                                              
		double division = Q1 / Q2;                                                                                              
		double squareroot = Q3 * Q3;                                                                                            
		                                                                                                                        
		System.out.println("The addition of two numbers " + Q1 + " and " + Q2 + " is equal to " + sum + ".");                   
		                                                                                                                        
		System.out.println("The subtraction of two numbers " + Q1 + " and " + Q2 + " is equal to " + sub + ".");                
		                                                                                                                        
		System.out.println("The multiplication of two numbers " + Q1 + " and " + Q2 + " is equal to " + multiply + ".");        
		                                                                                                                        
		System.out.println("The division of two numbers " + Q1 + " and " + Q2 + " is equal to " + division + ".");              
		                                                                                                                        
		System.out.println("The square of the number 3.9 is " + squareroot + ".");                                              
		                                                                                                                        
		int Width = 5;                                                                                                          
		int Height = 8;                                                                                                         
		int Perimeter = 2 * (Width + Height);                                                                                   
		int Area1 = Width * Height;                                                                                             
		                                                                                                                        
		System.out.println("The perimeter of a rectangle with width 5 and height 8 is equal to " + Perimeter                    
				+ " and the area is " + Area1 + ".");                                                                           
		                                                                                                                        
		                                                                                                                        
		                                                                                                                        
		                                                                                                                        
		                                                                                                                        

	}
	

}
