package com.syntax.class03;

public class CalendarProg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int month = 8;
		
		if (month==1) {
			System.out.println("January brings the snow!");
			
		}else if (month==2) {
			System.out.println("February brings the rain!");
		
		}else if (month==3) {
			System.out.println("March brings the breeeze!");
		
		}else if (month==4) {
			System.out.println("April brings the primrose sweet!");
		
		}else if (month==5) {
			System.out.println("May brings flocks of pretty lambs!");
		
		}else if (month==6) {
			System.out.println("June brings tulips, lillies, and roses!");
		
		}else if (month==7) {
			System.out.println("July brings cooling showers!");
		
		}else if (month==8) {
			System.out.println("August brings the sheaves of corn!");
		
		}else if (month==9) {
			System.out.println("September brings the fruit!");
		
		}else if (month==10) {
			System.out.println("October brings the pheasant!");
		
		}else if (month==11) {
			System.out.println("November winds are drear!");
			
		}else if (month==12) {
			System.out.println("December means the winter is here again!");
			
		} else {
			System.out.println("Invalid entry!");
		}
		
		
		

	}

}
