package com.syntax.class03;

public class MoreIfStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Define a variable to store a value of day
		 * 
		 * If day is Saturday --> "I'm having Java class today."
		 * Otherwise --> "I dont't know which class I have."
		 * "Maybe today is fun-day! Yay!"
		 */
		
		String day6 = "Saturday";
		
		
		if (day6.equals("Saturday")) {
			
			System.out.println("I'm having a Java class today!");
			System.out.println("I'm very excited!");
			System.out.println("I'm going to have fun with my classmates!");
		
		}else {
			
			System.out.println("I don't know which class I have.");
			System.out.println("Maybe today is fun-day! Yay!");
		}
		
		

	}

}
