package com.syntax.class03;

public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int num1 = 10;
	
	int num2 = 16;
	
	System.out.println(num1>num2);// Console returns a false value.
	
	System.out.println(num1<num2);// Console returns a true value.
	
	System.out.println(num1==num2);// (Comparison)--> Console returns a false value.
	
	System.out.println(num1=num2);// Reassigning
	
	System.out.println(num1!=num2);// (Inequality)--> Console returns a true value.
	
	boolean result = num1!=num2;
	
	System.out.println("Result is "+ result);// console returns a true value
	
	System.out.println(num1==num2);// Comparing
	
	
	
	

	}
	

}
